upOpt.filter:
/* 
    决定某文件是否需要上传
      可用的注入参数:
        - console, params, config, projName, projDir, srcDir, distDir, workDir...
        - filePath: Path of the uploading file
        - fileStreem: Stream of the uploading file
      查看所有可用参数:
        - console.log(this.queryAvailableArguments();
 */
function uploadFilter(filePath, projName) {     
    // 例子: 
    //   不上传 *.map 类的文件
    return !/\.map$/.test(filePath); 
}

upOpt.form:
/* 
    生成文件上传的表单.
      可用的注入参数:
        - console, params, config, projName, projDir, srcDir, distDir, workDir...
        - filePath: Path of the uploading file
        - fileStreem: Stream of the uploading file
      查看所有可用参数:
        - console.log(this.queryAvailableArguments();
 */
function uploadForm(fileStream, filePath) {
    // 例子:
    //   生成带有 fileDir, fileName, fileType, fileContents 四个字段的表单，其中 fileContents 为上传的文件内容
    var fileDir = filePath.split('/'),
        fileName = fileDir.pop().split('.'),
        fileType = fileName.length > 1 ? fileName.pop() : '';
    return {
        'fileDir': fileDir.join('/'),
        'fileName': fileName.join('.'),
        'fileType': fileType,
        'fileContents': fileStream
    };
}

upOpt.judge:
/*
    判断文件上传是否成功.
      可用的注入参数:
        - console, params, config, projName, projDir, srcDir, distDir, workDir...
        - filePath: Path of the uploading file
        - fileStreem: Stream of the uploading file
        - reponse: Reponse from the uploading server
      查看所有可用参数:
        - console.log(this.queryAvailableArguments();
 */
function uploadJudge(response) {
    // 例子: 
    //   根据是否返回 { status: 2 } 判断上传结果.
    try {
        var res = JSON.parse(response);
        return res.status == 2;
    } catch (e) {
        return false;
    }
}

