smOpt.mappingUrl:
/*
    传给 gulp-sourcemaps 插件 write 参数中的 
        sourceMappingURL 字段
 */
function mappingUrl(file) {
    // return 'http://asset-host.example.com/' + file.relative + '.map';
    return require('path').basename(file.path) + '.map';
}
